$(function(){$('a[href^="#"]').click(function(){var t=$(this).attr("href"),t=$('a[name="'+t.slice(1)+'"]').offset();$("body, html").animate({scrollTop:t.top},550)}),$(".product").hover(function(){$(this).find(".brands").stop(!0),$(this).find(".arrow").fadeToggle(50),$(this).find(".brands").slideToggle()});var r=Math.floor(1e3*Math.random());$("form").submit(function(t){t.preventDefault();var e=$(this);$.ajax({type:e.attr("method"),url:e.attr("action"),data:e.serialize()+"&num="+r,success:function(t){e[0].reset(),$(".form-msg").slideDown().text(t),setTimeout(function(){$(".form-msg").slideUp().text("")},3e3)},error:function(t){alert("An error occurred! Please resend yor message!")}})})});